const btnHamburger=document.querySelector("#btnHamburger"),firstSpan=btnHamburger.firstElementChild,menu=document.getElementById("overlay");btnHamburger.addEventListener("click",function(){btnHamburger.classList.contains("open")?(firstSpan.style.width="15px",btnHamburger.classList.remove("open"),menu.classList.remove("menu")):(firstSpan.style.width="26px",btnHamburger.classList.add("open"),menu.classList.add("menu"))}),menu.addEventListener("click",e=>{for(var t of menu.children)t.classList.remove("active-menu"),t.classList.remove("active-line");e.target.classList.add("active-menu"),e.target.classList.add("active-line")});